import "../index.scss"

import Skills from '../containers/Skills'
import About from '../containers/About'
import Projets from '../containers/Projets'

function Home() {

  const sections = document.querySelectorAll("span")
  const navLi = document.querySelectorAll("div section nav a")
  console.log(navLi)

  window.onscroll = () => {
      var current = "";
      var Y = window.scrollY;

      sections.forEach((section) => {
        const sectionTop = section.offsetTop;
        if (Y >= sectionTop - 137 ) {
          current = section.getAttribute("id"); }
      });

      navLi.forEach((a) => {
        a.classList.remove("active");
        if (a.classList.contains(current)) {
          a.classList.add("active");
        }
      });
    };

    return (
      <div className="home_wrapper">
        <span className="anchor" id="about"></span>
        <section className="about_section padding_section">
          <About/>
        </section>
        <span className="anchor" id="projets"></span>
        <section className="projets_section padding_section">
          <Projets/>
        </section>
        <span className="anchor" id="skills"></span>
        <section className="skills_section padding_section">
          <Skills/>
        </section>
      </div>
    );
  }

  export default Home
